mapper.select:
select *
from organization_relation
where uid = #{uid} and oid = #{id}

select uid
from organization_relation
where oid = #{id}

mapper.delete:
delete
from organization_relation
where uid = #{uid} and oid = #{oid}

mapper.update:
update organization
set
name = #{name},
intro = #{intro}
where id = #{id}
